import React from "react";
import { useNavigate } from "react-router-dom";
import "./FormEmailAutentic.css"
import {useState} from "react";




export default function FormEmailAutentic(){
    
    const [autChars, setAutChars] = useState(new Array(6).fill(null));

    const navigate = useNavigate();

    const handleSubmit = async(event)=>{
        event.preventDefault();
        const inputAut = autChars.join("");
        try{
            const response = await fetch('http://localhost:3000/autVerif',{
                method: 'POST',
                headers: {
                'Content-Type': 'application/json'
                 },
                body:JSON.stringify({inputAut})
            })

            if(!response.ok){
                throw new Error('Erro na requisição')
            }

            const data = await response.json();
            console.log(data)
        }catch(error){
            console.error('Erro:', error);
        }
        
    }
    const handleInputChange = (event, key) => {
        // Permitir apenas números no input
        newValue = event.target.value.replace(/[^0-9]/g, '');

        const updatedAutChars = [...autChars];
        updatedAutChars[key] = 
        setAutChars(autChars[key] = event.target.value);
    };

    return(
        <div className="container">
            <form onSubmit={handleSubmit}>
                <p>Para concluir a configuração da conta, insira o código que foi enviado para:</p>
                <div className="inputField">
                    <label htmlFor="email">Emailtestw@gmail.com</label>

                    
                    <div className="inputsAut">
                        {autChars.map((char,index) => (
                            <input key={index} type="text" className="autChar" maxLength={1} onInput={(event)=>handleInputChange(event,index)}/>
                        ))
                        }
                    </div>
                </div>
                <input type="submit" value="Continuar" className="submitBtn"/>
            </form>
        </div>

    )
}
